mapper.insert:
insert into course(
    cover_pic,
    title,
    author_id = #{authorId}
) values (
    #{title},
    #{coverPic},
    authorId
)

insert into course_chapter(
    desc,
    course_id, chapter_order, title
) values (
    #{desc},
    #{courseId}, #{chapterOrder}, #{title}
)

mapper.update:
update course set
title = #{title},
cover_pic = #{coverPic},
authorId = #{authorId}
where course_id = #{courseId}

mapper.delete:
delete from blog
where author_id = #{userId} and id in

    #{chapterId}

mapper.select:
select chapter_id, course_id, chapter_order, title, desc
from course_chapter where chapter_id in 

    #{chapterId}
