mapper.update:
update blog
set comment_cnt = comment_cnt + 1
where id = #{blogId}

update blog
set view_cnt = view_cnt + 1
where id = #{blogId}

update blog
set collect_cnt = collect_cnt + 1
where id = #{blogId}

update blog
set collect_cnt = collect_cnt - 1
where id = #{blogId}

update blog
set like_cnt = like_cnt + 1
where id = #{blogId}

update blog
set like_cnt = like_cnt - 1
where id = #{blogId}
